import { useState, useEffect } from 'react';
import axios from 'axios';

export default function InputTodo() {
  const [inputTodo, setInputTodo] = useState('');
  const [todoList, setTodoList] = useState([]);

  // 처음 렌더링 될 때 모든 데이터 가져오기(한번만)
  useEffect(() => {
    getData();
  }, []);

  // 모든 데이터 가져오는 함수
  const getData = async () => {
    const res = await axios({
      method: 'GET',
      url: 'http://localhost:8000/todos',
    });
    setTodoList(res.data.data);
  };

  // 데이터 추가하기
  const AddTodo = () => {
    // 새 객체에 데이터 추가
    const newTodo = {
      title: inputTodo,
      done: false,
    };
    // DB에 데이터 보내기
    const postData = async () => {
      const res = await axios({
        method: 'POST',
        url: 'http://localhost:8000/todo',
        data: newTodo,
      });
      console.log(res.data.data);
      setInputTodo('');
    };

    // 데이터 추가하고 다시 모든 데이터 불러오기
    postData();
    getData();
  };

  // 체크박스 on/off
  const toggleTodo = (id) => {
    setTodoList(
      todoList.map((todo) => {
        console.log(todo.id === id);
        // console.log(todo);
        // 내가 체크한 박스의 id가 todos에 있는 id와 동일하다면 todo의 나머지 요소는 변하지 않고 checked요소만 반대로 해준다.
        return todo.id === id ? { ...todo, done: !todo.done } : todo;
      })
    );
  };

  //   선택된 체크박스만 삭제
  const removeTodo = (id) => {
    const deleteData = async () => {
      const res = await axios({
        method: 'DELETE',
        url: `http://localhost:8000/todo/:${id}`,
        data: {
          id: Number(id),
        },
      });
      console.log('a');
    };
    deleteData();
  };

  return (
    <>
      <input value={inputTodo} onChange={(e) => setInputTodo(e.target.value)} />
      <button onClick={AddTodo}>추가</button>

      <ul>
        {todoList.map((todo) => {
          return (
            <li key={todo.id}>
              <input type='checkbox' checked={todo.done} onChange={() => toggleTodo(todo.id)} />
              {todo.title}
              <button onClick={() => removeTodo(todo.id)}>DELETE</button>
            </li>
          );
        })}
      </ul>
    </>
  );
}
