import { useState, useEffect } from 'react';
import axios from 'axios';
import { useRef } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashCan } from '@fortawesome/free-regular-svg-icons';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import EmptyList from './EmptyList';

//styled-component
const _wrapperBox = styled.div`
  margin: 0 auto;
  width: 35%;
  background-color: white;
  height: 100%;
`;

const _styledHeader = styled.div`
  display: flex;
  justify-content: center;
  height: 50px;
  align-items: center;
  background-color: skyblue;
  color: white;
`;

const _styledInput = styled.input`
  border: none;
  border-bottom: 2px solid skyblue;
  padding-bottom: 5px;
  width: 85%;
  outline: none;
  margin-right: 20px;
  margin-left: 7px;
`;

const _addButton = styled.button`
  width: 6%;
  border: none;
  background-color: #eee;
  border-radius: 5px;
`;

const _inputDiv = styled.div`
  margin-top: 20px;
  display: flex;
  padding: 10px;
`;

const _todoLengthDiv = styled.div`
  padding: 10px;
  margin-bottom: 10px;
  cursor: pointer;
`;

const _ulTag = styled.ul`
  list-style: none;
  padding: 0px 10px;
  margin: 0px;
`;

const _liTag = styled.li`
  display: flex;
  margin-bottom: 20px;
`;

export default function InputTodo() {
  const [inputTodo, setInputTodo] = useState('');
  const [todoList, setTodoList] = useState([]);

  const inputWriter = useRef();
  const updateInput = useRef({});

  // Ï≤òÏùå Ïã§ÌñâÏãú Î™®Îì† Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    const getData = async () => {
      const res = await axios({
        method: 'GET',
        url: 'http://localhost:8000/todos',
      });
      setTodoList(res.data.data);
    };
    getData();
  }, []);

  // Ìï†Ïùº Ï∂îÍ∞Ä Ìï®Ïàò
  const AddTodo = async () => {
    // ÎπàÍ∞íÏù¥ ÏïÑÎãê ÎñÑÎßå Ï∂îÍ∞Ä
    if (inputTodo !== '') {
      const newTodo = {
        title: inputTodo,
        done: false,
      };
      setInputTodo('');

      const postData = async () => {
        const res = await axios({
          method: 'POST',
          url: 'http://localhost:8000/todo',
          data: newTodo,
        });
        // Ïó¨Í∏∞ÏÑú ÏûêÍæ∏ Î¶¨Ïä§Ìä∏Í∞Ä ÏóÖÎç∞Ïù¥Ìä∏ ÏïàÎèºÎäî Ïò§Î•òÍ∞Ä ÏûàÏóàÎäîÎç∞ ÏïåÍ≥† Î≥¥Îãà Ïª®Ìä∏Î°§Îü¨ÏóêÏÑú Îç∞Ïù¥ÌÑ∞Î•º ÏïàÎÑòÍ≤®Ï£ºÍ≥† ÏûàÏóàÏùå... Ïã§ÌôòÍ∞Ä..
        setTodoList([...todoList, res.data.data]);
      };

      postData();
    } else {
      // ÎπàÍ∞íÏù¥Î©¥ inputÏóê Ìè¨Ïª§Ïä§ ÌïòÍ∏∞
      inputWriter.current.focus();
    }
  };

  // Ï≤¥ÌÅ¨Î∞ïÏä§ on/off
  const toggleTodo = (id) => {
    setTodoList(
      todoList.map((todo) => {
        // ÎÇ¥Í∞Ä Ï≤¥ÌÅ¨Ìïú Î∞ïÏä§Ïùò idÍ∞Ä todosÏóê ÏûàÎäî idÏôÄ ÎèôÏùºÌïòÎã§Î©¥ todoÏùò ÎÇòÎ®∏ÏßÄ ÏöîÏÜåÎäî Î≥ÄÌïòÏßÄ ÏïäÍ≥† checkedÏöîÏÜåÎßå Î∞òÎåÄÎ°ú Ìï¥Ï§ÄÎã§.
        return todo.id === id ? { ...todo, done: !todo.done } : todo;
      })
    );
    console.log(updateInput);
    updateInput.id.current.focus();
  };

  //   ÏÑ†ÌÉùÎêú Ï≤¥ÌÅ¨Î∞ïÏä§Îßå ÏÇ≠Ï†ú
  const removeTodo = (id) => {
    const deleteData = async () => {
      const res = await axios({
        method: 'DELETE',
        url: `http://localhost:8000/todo/:${id}`,
        data: {
          id: Number(id),
        },
      });
      // Ïó¨Í∏∞Îäî Ïôú res.dataÎ°ú Î∞õÏïÑÏïº ÌïòÍ≥† ÏúÑÏóê Î™®Îì† Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò¨ ÎïåÎäî res.data.dataÎ•º Í∞ÄÏ†∏ÏôÄÏïº ÌïòÏßÄ..
      setTodoList(res.data);
    };
    deleteData();
  };

  const updateTodoTitle = async (id, newTitle) => {
    const updatedTodoList = todoList.map((todo) => {
      if (todo.id === id) {
        return { ...todo, title: newTitle };
      }
      return todo;
    });
    setTodoList(updatedTodoList);
  };

  const handleInputKeyDown = async (e, id, newTitle) => {
    if (e.key === 'Enter') {
      const res = await axios({
        method: 'PATCH',
        url: `http://localhost:8000/todo/:${id}`,
        data: {
          title: newTitle,
          id: Number(id),
        },
      });
      setTodoList(
        todoList.map((todo) => {
          return todo.id === id ? { ...todo, done: false } : todo;
        })
      );
      alert('ÏóÖÎç∞Ïù¥Ìä∏ ÎêòÏóàÏäµÎãàÎã§.');
      inputWriter.current.focus();
    }
  };

  return (
    <_wrapperBox>
      <_styledHeader>My Todo Appüî•</_styledHeader>

      <_inputDiv>
        <_styledInput
          ref={inputWriter}
          value={inputTodo}
          onChange={(e) => setInputTodo(e.target.value)}
          placeholder='Add Todo here'
        />
        <_addButton onClick={AddTodo}>
          <FontAwesomeIcon icon={faPlus} />
        </_addButton>
      </_inputDiv>

      <_todoLengthDiv>‚úèÔ∏è{todoList.length} Todos</_todoLengthDiv>
      {todoList.length !== 0 ? (
        <_ulTag>
          {todoList.map((todo) => {
            return (
              <_liTag key={todo.id}>
                <input type='checkbox' checked={todo.done} onChange={() => toggleTodo(todo.id)} />
                {todo.done === true ? (
                  <_styledInput
                    type='text'
                    value={todo.title}
                    ref={(value) => (updateInput.current[todo.id] = value)}
                    onChange={(e) => updateTodoTitle(todo.id, e.target.value)}
                    onKeyDown={(e) => handleInputKeyDown(e, todo.id, e.target.value)}
                  />
                ) : (
                  <_styledInput type='text' value={todo.title} readOnly />
                )}
                <_addButton onClick={() => removeTodo(todo.id)}>
                  <FontAwesomeIcon icon={faTrashCan} />
                </_addButton>
              </_liTag>
            );
          })}
        </_ulTag>
      ) : (
        <EmptyList />
      )}
    </_wrapperBox>
  );
}
