import { useState, useEffect } from 'react';
import axios from 'axios';

export default function InputTodo() {
  const [inputTodo, setInputTodo] = useState('');
  const [todoList, setTodoList] = useState([]);
  useEffect(() => {
    const todoData = async () => {
      const res = await axios({
        method: 'GET',
        url: 'http://localhost:8000/todos',
      });
      setTodoList(res.data.data);
    };
    todoData();
  }, []);

  const AddTodo = () => {
    const newTodo = {
      title: inputTodo,
      done: false,
    };
    setTodoList([...todoList, newTodo]);
    setInputTodo('');
    const todoData = async () => {
      const res = await axios({
        method: 'POST',
        url: 'http://localhost:8000/todo',
        data: newTodo,
      });
      console.log(res.data.data);
    };
    todoData();
  };

  // 체크박스 on/off
  const toggleTodo = (id) => {
    setTodos(
      setTodoList.map((todo) => {
        // console.log(todo);
        // 내가 체크한 박스의 id가 todos에 있는 id와 동일하다면 todo의 나머지 요소는 변하지 않고 checked요소만 반대로 해준다.
        return todo.id === id ? { ...todo, checked: !todo.checked } : todo;
      })
    );
  };

  return (
    <>
      <input value={inputTodo} onChange={(e) => setInputTodo(e.target.value)} />
      <button onClick={AddTodo}>추가</button>

      <ul>
        {todoList.map((value) => {
          return (
            <li key={value.id}>
              <input type='checkbox' checked={value.done} onChange={() => toggleTodo(value.id)} />
              {value.title}
              <button onClick={removeTodo}>DELETE</button>
            </li>
          );
        })}
      </ul>
    </>
  );
}
