import { useState, useEffect } from 'react';
import axios from 'axios';

export default function InputTodo() {
  const [inputTodo, setInputTodo] = useState('');
  const [todoList, setTodoList] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const res = await axios.get('http://localhost:8000/todos');
        setTodoList(res.data.data);
      } catch (error) {
        console.error('데이터 가져오기 실패:', error);
      }
    };
    getData();
  }, []);

  const AddTodo = async () => {
    const newTodo = {
      title: inputTodo,
      done: false,
    };

    try {
      const res = await axios.post('http://localhost:8000/todo', newTodo);
      setInputTodo('');
      setTodoList([...todoList, res.data.data]);
    } catch (error) {
      console.error('데이터 추가 실패:', error);
    }
  };

  const toggleTodo = async (id) => {
    const updatedTodoList = todoList.map((todo) => {
      if (todo.id === id) {
        return { ...todo, done: !todo.done };
      }
      return todo;
    });

    try {
      await axios.put(`http://localhost:8000/todo/${id}`, { done: !todoList.find((todo) => todo.id === id).done });
      setTodoList(updatedTodoList);
    } catch (error) {
      console.error('데이터 업데이트 실패:', error);
    }
  };

  const removeTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/todo/${id}`);
      setTodoList(todoList.filter((todo) => todo.id !== id));
    } catch (error) {
      console.error('데이터 삭제 실패:', error);
    }
  };

  useEffect(() => {
    console.log(todoList);
  }, [todoList]);

  return (
    <>
      <input value={inputTodo} onChange={(e) => setInputTodo(e.target.value)} />
      <button onClick={AddTodo}>추가</button>

      <ul>
        {todoList.map((todo) => (
          <li key={todo.id}>
            <input type='checkbox' checked={todo.done} onChange={() => toggleTodo(todo.id)} />
            {todo.title}
            <button onClick={() => removeTodo(todo.id)}>DELETE</button>
          </li>
        ))}
      </ul>
    </>
  );
}
